script:
frappe.ui.form.on('Dashboard Chart', {
  onload: function (frm) {
    if (frm.doc.chart_name === "Target Chart") {
      frm.set_value("filters", JSON.stringify({ user_email: frappe.session.user }));
    }
  },
});

frappe.ui.form.on('Property', {
	status: function (frm) {
        if (frm.doc.status === 'On lease' || frm.doc.status === 'Off Lease in 3 Months') {
            // Show "price per month/day"
            frm.set_df_property('price_per_month_day', 'hidden', 0);
            frm.set_df_property('price_per_meter', 'hidden', 1);
        } else if (frm.doc.status === 'On Sale' || frm.doc.status === 'Sold' || frm.doc.status === 'Managed for customer') {
            // Show "price per meter"
            frm.set_df_property('price_per_month_day', 'hidden', 1);
            frm.set_df_property('price_per_meter', 'hidden', 0);
        } else {
            // Hide both if no match
            frm.set_df_property('price_per_month_day', 'hidden', 1);
            frm.set_df_property('price_per_meter', 'hidden', 1);
        }
    }
})

frappe.ui.form.on('Unit', {
	status: function (frm) {
        if (frm.doc.status === 'On Lease' || frm.doc.status === 'Off Lease in 3 Months') {
            // Show "price per month/day"
            frm.set_df_property('price_per_month_day', 'hidden', 0);
            frm.set_df_property('price_per_meter', 'hidden', 1);
        } else if (frm.doc.status === 'On Sale' || frm.doc.status === 'Sold' || frm.doc.status === 'Managed for customer') {
            // Show "price per meter"
            frm.set_df_property('price_per_month_day', 'hidden', 1);
            frm.set_df_property('price_per_meter', 'hidden', 0);
        } else {
            // Hide both if no match
            frm.set_df_property('price_per_month_day', 'hidden', 1);
            frm.set_df_property('price_per_meter', 'hidden', 1);
        }
    }
})

frappe.ui.form.on('Property', {
    refresh: function(frm) {
        // Add a custom button for Reservation
        frm.add_custom_button(__('Reserve'), function() {
            check_existing_reservation_and_open_dialog(frm);
        });

        // Add a custom button for Cancel Reservation
        frm.add_custom_button(__('Cancel Reservation'), function() {
            cancel_existing_reservation(frm);
        });
    }
});

function check_existing_reservation_and_open_dialog(frm) {
    // Check if a reservation already exists for this property
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Reservation',
            filters: {
                property_unit: frm.doc.name,
                status: 'Reserve'
            },
            fields: ['name']
        },
        callback: function(response) {
            if (response.message && response.message.length > 0) {
                frappe.msgprint({
                    message: __('A reservation already exists for this property.'),
                    indicator: 'orange',
                    alert: true
                });
            } else {
                open_reservation_dialog(frm);
            }
        }
    });
}

function open_reservation_dialog(frm) {
    // Create a dialog for the Reservation Doctype
    const dialog = new frappe.ui.Dialog({
        title: __('Make a Reservation'),
        fields: [
            {
                label: 'Property',
                fieldname: 'property',
                fieldtype: 'Link',
                options: 'Property',
                default: frm.doc.name,
                read_only: 1
            },
            {
                label: 'Reserved By',
                fieldname: 'reserved_by',
                fieldtype: 'Link',
                options: 'CRM Lead',
                reqd: 1 // Make this field mandatory
            },
            {
               fieldname: 'price',
               fieldtype: 'Int',
               label: 'Price',
               default: frm.doc.total_price,
               read_only: 1,
            },
            {
                label: 'Status',
                fieldname: 'status',
                fieldtype: 'Select',
                options: ['Reserve', 'Cancel'],
                default: 'Reserve',
                read_only: 1,


            },
            {
                label: 'Reservation Date',
                fieldname: 'reservation_date',
                fieldtype: 'Date',
                default: frappe.datetime.now_date()
            },
            {
               fieldname: 'national_id',
               fieldtype: 'Attach',
               label: 'National ID'
            },
            {
               fieldname: 'sales_person',
               fieldtype: 'Link',
               label: 'Sales Person',
               options: 'User'
            }
        ],
        primary_action_label: __('Submit'),
        primary_action: function(values) {
            // Call a server-side method to create the Reservation and update Property
            frappe.call({
                method: 'frappe.client.insert',
                args: {
                    doc: {
                        doctype: 'Reservation',
                        property_unit: values.property,
                        reserved_by: values.reserved_by,
                        status: values.status,
                        reservation_date: values.reservation_date,
                        price: values.price,
                        sales_person: values.sales_person,
                        national_id: values.national_id,
                    }
                },
                callback: function(response) {
                    if (response.message) {
                        frappe.msgprint({
                            message: __('Reservation created successfully.'),
                            indicator: 'green',
                            alert: true
                        });
                        // Update the Property status to "Booked"
                        frm.set_value('for_cancellation', frm.doc.status)
                        frm.set_value('status', 'Reserved');
                        frm.save();
                    }
                }
            });
            dialog.hide();
        }
    });

    dialog.show();
}

function cancel_existing_reservation(frm) {
    // Check if a reservation exists for the property
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Reservation',
            filters: {
                property_unit: frm.doc.name,
                status: 'Reserve'
            },
            fields: ['name']
        },
        callback: function(response) {
            if (response.message && response.message.length > 0) {
                const reservation_name = response.message[0].name;
                // Update the reservation status to "Cancel"
                frappe.call({
                    method: 'frappe.client.set_value',
                    args: {
                        doctype: 'Reservation',
                        name: reservation_name,
                        fieldname: 'status',
                        value: 'Cancel'
                    },
                    callback: function() {
                        frappe.msgprint({
                            message: __('Reservation canceled successfully.'),
                            indicator: 'green',
                            alert: true
                        });
                        // Update the Property status to "Available"
                        frm.set_value('status', frm.doc.for_cancellation);
                        frm.save();
                    }
                });
            } else {
                frappe.msgprint({
                    message: __('No active reservation exists for this property.'),
                    indicator: 'orange',
                    alert: true
                });
            }
        }
    });
}
