function openDocType(doctype) {
   // Check if ERPNext's `frappe` object is available
   if (typeof frappe !== 'undefined') {
       // Use ERPNext's built-in navigation method to open the DocType in the same desk page
       frappe.set_route('List', doctype);
   } else {
       console.log("ERPNext frappe object not found. Ensure this script is running within ERPNext.");
   }
}

